public class While {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int contador = 1;
		while (contador < 11) {
			System.out.println("Contador is: " + contador);
			contador++;
		}

	}

}
